// Exercises 2.6.-2.10.

import React, { useState } from "react";
import Form from "./components/Form";
import Persons from "./components/Persons";
import Alert from './components/Alert';
import Filter from './components/Filter'
import "./App.css";

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas' }
  ]);
  const [newName, setNewName] = useState('');
  const [ filter, setFilter ] = useState('')

  const addPerson = (e) => {
    e.preventDefault()
    console.log('adding persons', e.target)
    const personObject = {
      name: newName
    }
    setPersons(persons.concat(personObject))
    setNewName('')

    const checkPerson = persons.find(person =>
      person.name === personObject.name)
    if (checkPerson === persons.name) {
      Alert(personObject)
    }
  };

  const handleNameChange = (e) => {
    console.log(e.target.value)
    setNewName(e.target.value)
  };

  const handleFilter = (event) => {
    setFilter(event.target.value)
  };

  const personsAfterFilter =
    filter === ''  ? persons : persons.filter(person => person.name)

  return (
    <div>
      <h2>Phonebook</h2>
      <Filter
        filter={filter}
        handleFilter={handleFilter}
      />
        <Form onSubmit={addPerson} newName={newName} handleNameChange={handleNameChange} />
      <h2>Numbers</h2>
        <Persons persons={personsAfterFilter} />
    </div>
  )
};

export default App;
