import { useState, useEffect } from "react";
import axios from "axios";

const Weather = ({ capital }) => {
  const api_key = process.env.REACT_APP_API_KEY;
  const [weather, setWeather] = useState([]);

  useEffect(() => {
    axios.get(`http://api.weatherstack.com/current?access_key=${api_key}&query=${capital}`)
      .then(response => {
        console.log(response.data);
        setWeather(response.data);
      });
// eslint-disable-next-line
  }, [])

  if (weather.length > 0) {
    const currentWeather = weather[0].current
    return (
      <div>
        <h2>Weather in {capital}</h2>
        <p>
          <strong>temperature:</strong> {currentWeather.temperature} Celsius
        </p>
        <img src={currentWeather.weather_icons} alt="weather icon" />
        <p>
          <strong>wind:</strong> {currentWeather.wind_speed} km/h direction{" "}
          {currentWeather.wind_dir}
        </p>
      </div>
    )
  }
};

export default Weather;
