import React from "react";
import Country from "./Country";


const Countries = ({ countries, filterValue }) => {
  let filteredCountry = [];

  if (filterValue.length > 0) {
    filteredCountry = countries.filter(country =>
      country.name.toLowerCase().includes(filterValue.toLowerCase()))
    console.log(countries);
  } else {
    filteredCountry = countries
  }

  if (filteredCountry.length > 10) {
    return 'Too many matches, specify another filter';

  } else if (filteredCountry.length === 1) {
    return (
      <div>
        <h1>{filteredCountry[0].name}</h1>
          <p>capital {filteredCountry[0].capital}</p>
          <p>population {filteredCountry[0].population}</p>
        <h2>languages</h2>
          <ul>
            {filteredCountry[0].languages.map(language => <li key={language.name}>{language.name}</li>)}
          </ul>
        <img src={filteredCountry[0].flag} width="33%" height="33%" alt="" />
      </div>
        )
  } else {
    return (
      <div>
        <ul>
          {filteredCountry.map(country =>
            <Country
                key={country.name}
                country={country}
            />
          )}
        </ul>
      </div>
    )
  }
}

export default Countries;
